import React from "react";

export default function Controls({ onMove }) {
  return (
    <div className="controls">
      <button className="btn" onClick={() => onMove("UP")}>Up</button>
      <button className="btn" onClick={() => onMove("LEFT")}>Left</button>
      <button className="btn" onClick={() => onMove("DOWN")}>Down</button>
      <button className="btn" onClick={() => onMove("RIGHT")}>Right</button>
    </div>
  );
}
