import React from "react";

const posMatch = (p = [], r, c) => {
  if (!Array.isArray(p) || p.length === 0) return false;
  return p.some(pos => {
    if (!pos) return false;
    if (Array.isArray(pos)) return pos[0] === r && pos[1] === c;
    if (typeof pos === "object") {
      const rr = pos.r ?? pos.row ?? pos[0];
      const cc = pos.c ?? pos.col ?? pos[1];
      return rr === r && cc === c;
    }
    return false;
  });
};

export default function GameBoard({ board = [], newTiles = [], mergedTiles = [] }) {
  if (!Array.isArray(board) || board.length === 0) {
    return <div className="board-empty">No game state</div>;
  }

  const size = board.length;

  return (
    <div className={`board size-${size}`} role="grid" aria-label={`game-board-${size}x${size}`}>
      {board.map((row, rIdx) => (
        <div key={rIdx} className="board-row" role="row">
          {(Array.isArray(row) ? row : []).map((cell, cIdx) => {
            const value = Number(cell) || 0;
            const isNew = posMatch(newTiles, rIdx, cIdx);
            const isMerged = posMatch(mergedTiles, rIdx, cIdx);
            const classes = [
              "tile",
              value === 0 ? "empty" : "",
              isNew ? "new-tile" : "",
              isMerged ? "merge" : ""
            ].filter(Boolean).join(" ");

            return (
              <div
                key={cIdx}
                role="gridcell"
                aria-label={`tile-${rIdx}-${cIdx}-${value}`}
                className={classes}
                data-value={value > 0 ? value : 0}
              >
                <div className="tile-inner">{value > 0 ? value : ""}</div>
              </div>
            );
          })}
        </div>
      ))}
    </div>
  );
}
