import React, { useState, useEffect, useCallback } from "react";
import GameBoard from "./components/GameBoard";
import Controls from "./components/Controls";
import { FaUndo, FaRedo } from "react-icons/fa";
import { newGame, restart, move } from "./api/gameApi";

export default function App(){
  const [state, setState] = useState(null);
  const [size, setSize] = useState(4);
  const [moveDir, setMoveDir] = useState(null);
  const [history, setHistory] = useState([]);
  const [future, setFuture] = useState([]);

  useEffect(() => {
    const fetchNew = async () => {
      try {
        const res = await newGame(size);
        console.log("newGame response:", res?.data);
        const payload = res?.data || {};
        const board =
          Array.isArray(payload) ? payload
          : Array.isArray(payload.board) ? payload.board
          : Array.isArray(payload.state) && Array.isArray(payload.state.board) ? payload.state.board
          : payload.game && Array.isArray(payload.game.board) ? payload.game.board
          : null;

        setState({
          ...payload,
          board: board ?? Array.from({ length: size }, () => Array.from({ length: size }, () => 0))
        });
      } catch (err) {
        console.error("newGame failed", err);
        setState({ board: Array.from({ length: size }, () => Array.from({ length: size }, () => 0)) });
      }
    };
    fetchNew();
  }, [size]);

  useEffect(()=>{
    const handleKey = (e) => {
      const map = {ArrowUp:'UP', ArrowDown:'DOWN', ArrowLeft:'LEFT', ArrowRight:'RIGHT'};
      if(map[e.key]) { doMove(map[e.key]); }
    }
    window.addEventListener('keydown', handleKey);
    return ()=> window.removeEventListener('keydown', handleKey);
  }, [state]);

  const doMove = useCallback(async (dir)=>{
    if (!state) return;
    setMoveDir(dir);
    setHistory(h => [...h, state]);
    setFuture([]);

    const gameId = state.gameId ?? state.id ?? (state.game && state.game.id) ?? null;
    try {
      const res = await move(gameId, dir);
      const payload = res?.data ?? res;
      setState(prev => ({
        ...prev,
        ...payload,
        board: payload.board ?? prev.board
      }));
    } catch (err) {
      // better error visibility
      console.error("move failed", {
        message: err.message,
        status: err.response?.status,
        body: err.response?.data,
        headers: err.response?.headers
      });
    }
  }, [state]);

  const handleUndo = () => {
    if (history.length === 0) return;
    setFuture(f => [state, ...f]);
    setState(history[history.length - 1]);
    setHistory(history.slice(0, -1));
  };

  const handleRedo = () => {
    if (future.length === 0) return;
    setHistory(h => [...h, state]);
    setState(future[0]);
    setFuture(future.slice(1));
  };

  const handleRestart = () => {
    restart(size).then(res => {
      const d = res?.data ?? res;
      setState(prev => ({
        ...prev,
        ...d,
        board: d.board ?? prev.board ?? Array.from({ length: size }, () => Array.from({ length: size }, () => 0))
      }));
    }).catch(err => console.error("restart failed", err));
  };

  const handleNewSize = (s)=> {
    setSize(s);
    newGame(s).then(res => {
      const d = res?.data ?? res;
      setState({
        ...d,
        board: d.board ?? Array.from({ length: s }, () => Array.from({ length: s }, () => 0))
      });
    }).catch(err => console.error("newGame failed", err));
  };

  if (!state) return <div>Loading...</div>;

  return (
  <div className="app-row">
    {/* Instructions Card */}
    <div className="instructions">
      <h2>How to Play</h2>
      <ul>
        <li>Use arrow keys or buttons to move tiles.</li>
        <li>Merge tiles with the same number.</li>
        <li><strong>Any single tile that contains 2048 is a win!</strong></li>
        <li>Undo/Redo your moves anytime.</li>
      </ul>
    </div>

    {/* Main Game Card */}
    <div className="app">
      <h1>2048</h1>
      <div className="header">
        <div>Score: {state.score}</div>
        <div>
          <label>Size: </label>
          <select value={size} onChange={e=>handleNewSize(parseInt(e.target.value))}>
            <option value={3}>3x3</option>
            <option value={4}>4x4</option>
            <option value={5}>5x5</option>
          </select>
          <button onClick={handleRestart}>Restart</button>
        </div>
      </div>

      <GameBoard
        board={Array.isArray(state.board) ? state.board : Array.from({ length: size }, () => Array.from({ length: size }, () => 0))}
        newTiles={state.newTiles || []}
        mergedTiles={state.mergedTiles || []}
      />

      <Controls onMove={doMove} />

      {state.won && <div className="overlay">You Win!</div>}
      {state.over && <div className="overlay">Game Over</div>}
    </div>

    {/* Side Controls Card */}
    <div className="side-controls">
      <button className="icon-btn" onClick={handleUndo}><FaUndo /></button>
      <button className="icon-btn" onClick={handleRedo}><FaRedo /></button>
    </div>
  </div>
);

}
